import axios from "axios";

const ParcelContext = React.createContext();

class ParcelProvider extends Component {
    state={
        monthly_data: [],
        loading: true,
    }
    componentDidMount(){
        this.getData
    }

    getData = () => {
    return new Promise(async (resolve,reject) => {
        try{
            const res = await axios.get("http://localhost:5000/api/delivery/average/send-type/March");
            this.monthly_data = res.data
            resolve(
                this.monthly_data.map(element => ({ ...element,}))
                );
            }catch(err){
                reject(err);
            }
        });
    }

    render() {
        return (
        <ParcelContext.Provider value={this.state}>
            {this.props.children}
        </ParcelContext.Provider>
        )
    }
    }

const ParcelConsumer = ParcelContext.Consumer;

